      ,ui.value as serial_number
      ,uim.value as mac_address
      ,em.model_number1
      ,l.radius_username
      ,sci.config_name as config_rev
  from unique_identifier ui
  join unique_identifier uim
       on uim.equipment_id = ui.equipment_id
       and uim.unique_identifier_type = 'MAC ADDRESS'
